mapper.insert:
insert into bscollect ( UserId, BookId
  )
values ( #{userid,jdbcType=INTEGER}, #{bookid,jdbcType=INTEGER}
  )

mapper.update:
update bscollect
set UserId = #{userid,jdbcType=INTEGER},
  BookId = #{bookid,jdbcType=INTEGER}
where CollectId = #{collectid,jdbcType=INTEGER}

mapper.select:
select CollectId, UserId, BookId
from bscollect
where CollectId = #{collectid,jdbcType=INTEGER}

select CollectId, UserId, BookId
  from bscollect
  where UserId = #{userId,jdbcType=INTEGER}

select CollectId, UserId, BookId
  from bscollect
  where UserId = #{userid,jdbcType=INTEGER} and bookId = #{bookid,jdbcType=INTEGER}
