mapper.update:
update bsnotice
set NoticTitle = #{notictitle,jdbcType=VARCHAR},
  NoticeTime = #{noticetime,jdbcType=TIMESTAMP},
  BookId = #{bookid,jdbcType=INTEGER}
where NoticeId = #{noticeid,jdbcType=INTEGER}

mapper.select:
select NoticeId, NoticTitle, NoticeTime, BookId
from bsnotice
where NoticeId = #{noticeid,jdbcType=INTEGER}
